class Piece:
    def __init__(self,type) -> None:
        match type:
            case 0:
                self.coords = set(((0,0),(1,0),(2,0),(3,0)))
                self.korkeus = 1
            case 1:
                self.coords = set(((1,0),(0,1),(1,1),(2,1),(1,2)))
                self.korkeus = 3
            case 2:
                self.coords = set(((2,0),(2,1),(0,2),(1,2),(2,2)))
                self.korkeus = 3
            case 3:
                self.coords = set(((0,0),(0,1),(0,2),(0,3)))
                self.korkeus = 4
            case 4:
                self.coords = set(((0,0),(1,0),(0,1),(1,1)))
                self.korkeus = 2

class Place:
    def __init__(self,korkeus) -> None:
        self.kartta = [["X", ".", ".", ".", ".", ".", "." ,".", "X"] for _ in range(korkeus)]
        self.kartta.append(["X"]*9)
        self.korkeus = korkeus

    def tulosta(self):
        for i in self.kartta:
            print("".join(i))

    def osuuko(self,piece,x,y,dx,dy):
        for px,py in piece.coords:
            #print([y+py+dy,x+px+dx])
            if self.kartta[y+py+dy][x+px+dx] != ".":
                return True
        return False

    def liikutaAlas(self,piece,x,y):
        if self.osuuko(piece,x,y,0,1):
            for px, py in piece.coords:
                self.kartta[y+py][x+px] = "#"
            return -1 , y
        return x , y + 1

    def LiikutaSivu(self,piece,x,y,s):
        if not self.osuuko(piece,x,y,s,0):
            return  x + s , y
        return x , y
       

